function createFib(){
    var arr = [];
    return function(n){
        var num = arr[n];
        if(!num){
            if(n <= 2){
                num = 1;
            }else{
                num = arguments.callee(n - 1) + arguments.callee(n - 2);
            }
            arr[n] = num;
        }
        return num;
    }
}
